for $value at $p in ( for $time in fn:duplicate-values(( 
         xs:time('12:00:00'), xs:time('12:00:00'), xs:time('20:00:00'), 
         xs:time('01:00:00+12:00'), xs:time('02:00:00+13:00'))) 
         order by $time return $time ) 
         return adjust-time-to-timezone($value, (xs:dayTimeDuration("PT0S")[$p]))

for $value in fn:duplicate-values(( 
         xs:string('a'), xs:string('a'), xs:string('b'), xs:string('a'),  
         xs:string(''))) order by string($value) return $value

for $value in fn:duplicate-values(
         ( xs:gYear('2008'), xs:gYear('2008'), xs:gYear('1972'), xs:gYear('2008') )) 
         order by string($value) return $value

for $value in fn:duplicate-values(( 
         xs:gMonthDay('--06-16'), xs:gMonthDay('--06-16'), 
         xs:gMonthDay('--12-15'))) order by string($value) return $value
